#f = [4, 7, -1998] # Equation for non-complex numbersf=[4, 7, 1998] # Equation for complex numbersff = [8, 7]#x = [5] # Initial guess for non-complex numbersx = [1 + i] # Initial guess for complex numberserrors = [200]i = 1while errors > 0.00001  x_new = x(i) - polyval(f, x(i))/polyval(ff, x(i))  x = [x, x_new]  errors = [errors, abs(x(i) - x_new)]  i++endwhile  x_root = roots(f)plot(errors)title("Error graph")xlabel("Number of steps")ylabel("Error")clear